import { FacturaCompleta } from './../entidad/factura-completa';
import { FacturaService } from './../servicio/factura.service';
import { Vendedor } from './../entidad/vendedor';
import { DetalleVenta } from './../entidad/detalle-venta';
import { Factura } from './../entidad/factura';

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';

import { LoginVendedorService } from '../servicio/login-vendedor.service';
import { Cliente } from '../entidad/cliente';
import { Producto } from '../entidad/producto';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import { FacturaRespuesta } from '../entidad/factura-respuesta';
import { DetalleVentaConImpuestos } from '../entidad/detalle-venta-con-impuestos';
import { Impuesto } from '../entidad/impuesto';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-vendedor',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './vendedor.component.html',
  styleUrls: ['./vendedor.component.css']
})
export class VendedorComponent {

  Factura: Factura = new Factura();
  DetalleVenta: DetalleVenta = new DetalleVenta();
  DetalleVentas: DetalleVentaConImpuestos[] = [];

  Vendedores: Vendedor[] = [];
  clientes: Cliente[] = [];
  productos: Producto[] = [];
  FacturaCompleta: FacturaCompleta[] = [];

  cedulaV: number;
  CedulaC: number;
  idProducto: number = 0;
  nombrePro: string;
  mostrarBotonDescarga: boolean = false;

  constructor(
    private router: Router,
    private vendedorService: LoginVendedorService,
    private FacturaService: FacturaService
  ) {
    this.Factura.total = 0;
  }

  Registro() {
    this.router.navigate(['/registro']);
  }

  vendedor() {
    
    if (!/^\d+$/.test(this.cedulaV.toString())) {
      alert('âš ï¸ Solo se permiten nÃºmeros en la cÃ©dula del vendedor.');
      return; 
    }
  
    this.vendedorService.buscar(this.cedulaV).subscribe(
      dato => {
        this.Vendedores = Array.isArray(dato) ? dato : [dato];
        console.log("Vendedor encontrado:", this.Vendedores);
  
        if (!this.Vendedores.length) {
          alert('âš ï¸ Vendedor no encontrado.');

        }
      },
      error => {
        console.error('Error al obtener el vendedor:', error);
        alert('âŒ Error al obtener el vendedor.');
        this.Vendedores = [];
      }
    );
  }
  
  cliente() {
    
    if (!/^\d+$/.test(this.CedulaC.toString())) {
      alert('âš ï¸ Solo se permiten nÃºmeros en la cÃ©dula del cliente.');
      return;
    }
  
    this.vendedorService.buscar1(this.CedulaC).subscribe(
      dato => {
        if (!dato) { 
         
          alert('âš ï¸ Cliente no encontrado.');
          this.router.navigate(['/registro'])
          this.clientes = [];
          return;
        }
  
        this.clientes = Array.isArray(dato) ? dato : [dato];
  
        if (this.clientes.length === 0 || !this.clientes[0]) {
          alert('âš ï¸ Cliente no encontrado.');
          this.clientes = [];
        } else {
          console.log("Cliente encontrado:", this.clientes);
          alert(JSON.stringify(dato, null, 2));
        }
      },
      error => {
        console.error('Error al obtener el cliente:', error);
        alert('âŒ Error al obtener el cliente.');
        this.clientes = [];
      }
    );
  }
  
  
  

  buscarProducto() {
    this.FacturaService.buscarProducto(this.idProducto, this.nombrePro).subscribe(
      dato => {
        this.productos = Array.isArray(dato) ? dato : [dato];
        console.log("Productos encontrados:", this.productos);
      },
      error => {
        console.error("Error al obtener el producto", error);
        this.productos = [];
      }
    );
  }


  agregarProducto() {
    // Validar la cantidad primero
    if (
      typeof this.DetalleVenta.cantidad !== 'number' || 
      !Number.isInteger(this.DetalleVenta.cantidad) ||  
      this.DetalleVenta.cantidad <= 0
    ) {
      alert('âš ï¸ Ingrese una cantidad vÃ¡lida.');
      return; 
    }
  
    // Crear el nuevo detalle
    const nuevoDetalle: DetalleVentaConImpuestos = {
      producto: this.DetalleVenta.producto,
      cantidad: this.DetalleVenta.cantidad,
      factura: this.Factura,
      impuestos: [] // puedes llenar esto si ya tienes los impuestos del producto
    };
  
    // Agregar el producto solo si la cantidad es vÃ¡lida
    this.DetalleVentas.push(nuevoDetalle);
  
    // Resetear el detalle temporal
    this.DetalleVenta = new DetalleVenta();
  }
  
  

  crearFacturaCompleta() {
    this.Factura.fechaFactura = new Date();
  
    const facturaCompleta: FacturaCompleta = {
      factura: this.Factura,
      detalles: this.DetalleVentas
    };
  
    const totalVisual = facturaCompleta.detalles.reduce((sum, d) => {
      return sum + (d.producto?.precioCompra || 0) * (d.cantidad || 0);
    }, 0);
  
    // ðŸ”¥ Primero intentamos guardar la factura
    this.FacturaService.crearFactura(facturaCompleta).subscribe(
      (facturaRespuesta: FacturaRespuesta) => {
        // âœ… Se guardÃ³ correctamente
        this.Factura = facturaRespuesta.factura;
        this.DetalleVentas = facturaRespuesta.detalles;
        this.mostrarBotonDescarga = true;
  
        // âœ… Ahora sÃ­ mostramos el resumen
        const resumen = `
  Factura:
    Cliente: ${facturaRespuesta.factura.cliente?.nombre1} - CÃ©dula: ${facturaRespuesta.factura.cliente?.cedulaC}
    Vendedor: ${facturaRespuesta.factura.vendedor?.nombre} - CÃ©dula: ${facturaRespuesta.factura.vendedor?.cedulaV}
    Total: $${totalVisual.toFixed(2)}
  
  Detalles:
  ${facturaRespuesta.detalles.map((d, i) =>
          `  ${i + 1}. ${d.producto?.nombre} - Cantidad: ${d.cantidad} - Precio Unitario: $${d.producto?.precioCompra}`
        ).join('\n')}
        `;  
        alert(facturaRespuesta.mensaje);
        alert(resumen);
      },
      (error) => {
        console.error('Error al crear la factura:', error);
        alert(error.error);
  
        // ðŸ§¹ Limpiar los datos si falla
        this.DetalleVentas = [];
        this.productos = [];
        this.nombrePro = "";
        this.idProducto = 0;
      }
    );
  }
  

  generarPDF() {


    const logoBase64 = 'data:image/png;base64, '

    
    const doc = new jsPDF();
    const factura = this.Factura;
    const detalles = this.DetalleVentas;

    

    const fechaHora = new Date().toLocaleString('es-CO', {
      day: '2-digit',
      month: 'long',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });

    
    // ----------------- ENCABEZADO -----------------
  doc.addImage(logoBase64, 'PNG', 14, 10, 18, 18); // imagen a la izquierda

  doc.setFontSize(16);
  doc.text('SUPER MARKET', 35, 18); // texto alineado con la imagen, un poco mÃ¡s arriba

  doc.setFontSize(10);
  doc.text('NIT: 900123456-7', 35, 24); // justo debajo del nombre
  doc.text('DirecciÃ³n: Calle 123 #45-67, La Dorada Caldas, Colombia', 35, 29);
  doc.text('ResoluciÃ³n DIAN No. 18764000012345 de 2024', 35, 34);

  doc.setLineWidth(0.3);
  doc.line(14, 38, 195, 38);

  // ------------ SECCIÃ“N DE DATOS ------------
  let y = 44; // empezamos despuÃ©s de la lÃ­nea horizontal

  doc.setFontSize(12);
  doc.text(`FACTURA POS No.: ${factura.idFactura}`, 14, y);
  y += 8;

  doc.setFontSize(10);
  doc.text(`Fecha y hora de expediciÃ³n: ${fechaHora}`, 14, y);
  y += 8;

  doc.setFontSize(11);
  doc.text('Datos del Vendedor:', 14, y);
  y += 6;
  doc.setFontSize(10);
  doc.text(`Nombre Completo: ${factura.vendedor?.nombre || ''}`, 14, y);
  y += 6;
  doc.text(`CÃ©dula: ${factura.vendedor?.cedulaV || ''}`, 14, y);
  y += 10;

 

  doc.setFontSize(11);
  doc.text('Datos del Cliente:', 14, y);
  y += 6;
  doc.setFontSize(10);
  const nombreCliente = `${factura.cliente?.nombre1 || ''} ${factura.cliente?.nombre2 || ''} ${factura.cliente?.apellido1 || ''} ${factura.cliente?.apellido2 || ''}`.trim();
  doc.text(`Nombre Completo: ${nombreCliente}`, 14, y);
  y += 6;
  doc.text(`CÃ©dula: ${factura.cliente?.cedulaC || ''}`, 14, y);
  y += 6;
  doc.text(`DirecciÃ³n: ${factura.cliente?.direccion || ''}`, 14, y);
  y += 6;
  doc.text(`Email: ${factura.cliente?.correo || ''}`, 14, y);
  y += 10;


    // ----------------- TABLA -----------------
    autoTable(doc, {
      startY: y,
      head: [['Producto', 'Cantidad', 'Precio Sin IVA', 'Precio con IVA', 'Subtotal']],
      body: detalles.map(detalle => {
        const nombreProducto = detalle.producto?.nombre || '';
        const cantidad = detalle.cantidad || 0;
        const precioSinIva = detalle.producto?.precioUnitario || 0;
        const precioConIva = detalle.producto.precioCompra
        const subtotal = precioConIva * cantidad;


        const listaImpuestos = detalle.impuestos?.length
          ? detalle.impuestos.map(i => `${i.nombre} (${i.claveporcentaje}%)`).join(', ')
          : 'Sin impuestos';

        return [
          `${nombreProducto}\nImp: ${listaImpuestos}`,
          cantidad.toString(),
          `$${precioSinIva.toFixed(2)}`,
          `$${precioConIva.toFixed(2)}`,
          `$${subtotal.toFixed(2)}`
        ];
      }),
      styles: {
        fontSize: 9,
        cellPadding: 1.5,
        halign: 'center',
      },
      headStyles: {
        fillColor: [200, 200, 200],
        textColor: 20,
        fontStyle: 'bold',
        halign: 'center',
      },
      columnStyles: {
        0: { halign: 'left', cellWidth: 70 },
        1: { cellWidth: 20 },
        2: { cellWidth: 30 },
        3: { cellWidth: 30 },
        4: { cellWidth: 30 },
      },
    });

    const finalY = (doc as any).lastAutoTable.finalY || y + 10;

    const total = detalles.reduce((sum, detalle) => {
      const precioSinIva = detalle.producto?.precioUnitario || 0;
      const porcentajeTotalIva = detalle.impuestos?.reduce((acc, imp) => acc + (imp.claveporcentaje || 0), 0) || 0;
      const precioConIva = precioSinIva + (precioSinIva * porcentajeTotalIva / 100);
      return sum + precioConIva * (detalle.cantidad || 0);
    }, 0);

    doc.setFontSize(12);
    doc.setFont('helvetica', 'bold');
    doc.text(`Total a pagar: $${total.toFixed(2)}`, 14, finalY + 10);
    console.log('PDF generado y guardado con nombre: factura.pdf');
    console.log('Factura:', factura);
console.log('Detalles:', detalles);
console.log('Detalles:',);
    doc.save('factura.pdf');
  }
}

